var prediction = msg.payload.prediction;
var text = msg.payload.text;

// Aggiungi il campo text al messaggio
msg.text = text;

// Check se la predizione è 1 o 0
if (prediction === 1) {
    msg.payload = "Prediction is 1";
} else if (prediction === 0) {
    msg.payload = text;
} else {
    msg.payload = "Invalid prediction value";
}

// Restituisci il messaggio aggiornato
return msg;
